// src/pages/user/home/Home.js
import React from 'react';
import { Link } from 'react-router-dom';
import './Home.css'; // We'll create this CSS file next

const Home = () => {
  // Sample featured products data
  const featuredProducts = [
    {
      id: 1,
      name: 'Premium Cotton Shirt',
      price: 29.99,
      image: '/images/shirt1.jpg',
      category: 'mens/shirts'
    },
    {
      id: 2,
      name: 'Designer Handbag',
      price: 89.99,
      image: '/images/handbag1.jpg',
      category: 'accessories/bags'
    },
    {
      id: 3,
      name: 'Running Shoes',
      price: 59.99,
      image: '/images/shoes1.jpg',
      category: 'shoes/sports'
    }
  ];

  // Sample categories data
  const categories = [
    { name: "Men's Fashion", path: "mens", image: "/images/mens-category.jpg" },
    { name: "Women's Fashion", path: "womens", image: "/images/womens-category.jpg" },
    { name: "Kid's Collection", path: "kids", image: "/images/kids-category.jpg" }
  ];

  return (
    <div className="home-page">
      {/* Hero Banner */}
      <section className="hero-banner">
        <div className="hero-content">
          <h1>Summer Sale Up to 50% Off</h1>
          <p>Discover our new collection</p>
          <Link to="/mens/shirts" className="shop-now-btn">
            Shop Now
          </Link>
        </div>
      </section>

      {/* Featured Categories */}
      <section className="categories-section">
        <h2>Shop by Category</h2>
        <div className="categories-grid">
          {categories.map((category) => (
            <Link to={category.path} key={category.name} className="category-card">
              <img src={category.image} alt={category.name} />
              <h3>{category.name}</h3>
            </Link>
          ))}
        </div>
      </section>

      {/* Featured Products */}
      <section className="featured-products">
        <h2>Featured Products</h2>
        <div className="products-grid">
          {featuredProducts.map((product) => (
            <div key={product.id} className="product-card">
              <Link to={product.category}>
                <img src={product.image} alt={product.name} />
                <h3>{product.name}</h3>
                <p>${product.price.toFixed(2)}</p>
              </Link>
              <button className="add-to-cart-btn">Add to Cart</button>
            </div>
          ))}
        </div>
      </section>

      {/* Promotional Banner */}
      <section className="promo-banner">
        <div className="promo-content">
          <h2>New Arrivals</h2>
          <p>Discover the latest trends in fashion</p>
          <Link to="/womens" className="explore-btn">
            Explore Now
          </Link>
        </div>
      </section>
    </div>
  );
};

export default Home;
